package test;import java.io.File;import java.io.FileNotFoundException;import java.util.*;/*Or Kravitz 308248293   Nadav Voikovitch 308526508 */public class main {    public static void main(String args[]) throws FileNotFoundException {        for (int j = 0; j < 5; j++) {        String fileNum = "";            String input = "input" + (j+1) + ".txt";            File file = new File(input);            try (Scanner scanner = new Scanner(file)) {                fileNum = scanner.nextLine();                int num = Integer.parseInt(fileNum);                String[] s;                List<Proccess> list = new ArrayList<Proccess>();                for (int i = 0; i < num; i++) {                    fileNum = scanner.nextLine();                    s = fileNum.split(",");                    list.add(new Proccess(Integer.parseInt(s[0]), Integer.parseInt(s[1])));                }                List<Proccess> sList = new ArrayList<>();                for (Proccess p : list) {                    sList.add(new Proccess(p.arriveTime, p.BurstTime));                }                sList.sort(new SortById());                Algorithms myAlgorithms = new Algorithms();                System.out.println("\n"+ input +":");                myAlgorithms.FCFS(sList);                myAlgorithms.LCFSnp(sList);                myAlgorithms.LCFSp(sList);                myAlgorithms.RR(sList);                myAlgorithms.SJF(sList);                System.out.println("_________________________________________ ");            } catch (NumberFormatException e) {                e.printStackTrace();            }        }    }}